function b = setupKeys_PWA()% keyboard set upKbName('UnifyKeyNames');%b.resp = KbName({'0','1','4','7','.','3','6','9','q'}); %'q' to quit should be the last oneb.respKeyNames = {'0','7','.','9','q'}; %'q' to quit should be the last oneb.resp = KbName(b.respKeyNames); b.reportedCategory = [2 3 2 3 NaN]; %2=artifical; 3=naturalb.side = [1 1 2 2 0];% b.stim1 = [1 2 5 6];% b.stim2 = [3 4 7 8];% b.side = [1 1 1 1 2 2 2 2 0];% b.conf = [-2 -1 1 2 -2 -1 1 2]; %-2=sure no target; -1=maybe no; 1=maybe yes; 2=sure yes% % b.reportedCategory = NaN(size(b.resp));% b.reportedCategory(b.stim1) = 3; %categories 1=pseudoword, 2=nonliving, 3=living // TWO AND THREE REVERSED: 2= artificial 3= natural% b.reportedCategory(b.stim2) = 2;b.quit = numel(b.resp);%If we want keys to correspond to reports of 'absent' and 'present', let's%say that 'stim2' is the same as presb.abst = b.reportedCategory==2;b.pres = b.reportedCategory==3;otherKeys = KbName({'c','v','d','q','a','r','1!','escape','space','return','Return'});  FlushEvents('keyDown');%Restrict keyboard so nothing works except necessary keysRestrictKeysForKbCheck([b.resp otherKeys]);% start GetChar listening/ % input argument 2  will suppress output of keypresses to the Matlab windowsListenChar(2);